  <section className="section" id="about" ref={ref}>
    <div className="container mx-auto">
    <div>
    <div className="flex-1 bg-about bg-contain bg-no-repeat h-[640px]
    mix-blend-lighten bg-top"></div>
    <div>
    <h2>About me.</h2>
