import React from 'react';
import { BsArrowUpRight } from 'react-icons/bs';
import { motion } from 'framer-motion';
import { fadeIn } from '../variants';

const services = [
  {
    name: 'Java Developer',
    description:
      'Lorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adip.',
    link: 'Learn more',
    image: 'path-to-java-image.jpg', // Add the actual path to your Java Developer image
  },
  {
    name: 'Frontend Developer',
    description:
      'Lorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adip.',
    link: 'Learn more',
    image: 'path-to-frontend-image.jpg', // Add the actual path to your Frontend Developer image
  },
  // Add similar objects for other services
];

const Services = () => {
  return (
    <section className="section" id="services">
      <div className="container mx-auto">
        <div className="flex flex-col lg:flex-row">
          <div className="flex-1 lg:bg-services lg:bg-bottom bg-no-repeat mix-blend-lighten mb-12 lg:mb-0 ">
            <h2 className="h2 text-accent mb-6">What I Do</h2>
            <h3 className="h3 max-w-[455px] mb-16">
              Lorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adipLorem ipsum dolor sit amet, consectetur adipLorem.
            </h3>
            <button className="btn btn-sm">See my work</button>
          </div>
          <div className="flex-1">
            <div className="flex flex-wrap gap-8">
              {services.map((service, index) => (
                <div key={index} className="flex-1 max-w-[300px]">
                  <img
                    src={service.image}
                    alt={service.name}
                    className="w-full h-auto rounded"
                  />
                  <h4 className="h4 text-accent mt-4 mb-2">{service.name}</h4>
                  <p className="text-gray-500">{service.description}</p>
                  <a href="#" className="text-accent mt-2 inline-flex items-center">
                    {service.link} <BsArrowUpRight className="ml-1" />
                  </a>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Services;
