import React from 'react';
import CountUp from 'react-countup';
import { useInView } from 'react-intersection-observer';
import {motion} from 'framer-motion';
import { fadeIn } from '../variants';
const About = () => {
  const [ref, inView] =useInView({
    threshold:0.5,
  })
  return (
  <section className="section" id="about" ref={ref}>
    <div className="container mx-auto">
    <div className="flex flex-col gap-y-10 lg:flex-row lg:items-center 
    lg:gap-x-20 lg:gap-y-0 h-screen">
    <motion.div
    variants={fadeIn('right',0.3)} 
            initial="hidden"
            whileInView={'show'} 
            viewport={{once:false,amount:0.3}}
  className="flex-1 bg-about bg-contain bg-no-repeat h-[640px] mix-blend-lighten bg-top"
  style={{
    width: '500px',
    height: '500px', // You can adjust the height to your preference
    borderRadius: '100%',
  }}
>
  {/* Your content goes here */}
</motion.div>

    <motion.div 
    variants={fadeIn('left',0.5)} 
            initial="hidden"
            whileInView={'show'} 
            viewport={{once:false,amount:0.3}}
    className='flex-1'>
    <h2 className='h2 text-accent'>About me.</h2>
    <h3 className='h3 mb-4'>lorem ipsum dolor sit amet, consectetur</h3>
    <p className='mb-6'>Lorem ipsum dolor sit amet, consectet Lorem ipsum dolor sit amet, 
        consectetLorem ipsum dolor sit amet, consectetLorem ipsum dolor sit amet, 
        consectetLorem ipsum dolor sit amet, consectet.
        </p>
        <div className="flex gap-x-6 lg:gap-x-10 mb-12">
        <div>
        <div className="text-[40px] font-tertiary text-gradient
        mb-2">
        {
          inView ?
          <CountUp start={0} end={13} duration={3} /> :null}+
        </div>
        <div className="font-primary text-sm tracking-[2px]">Cources <br/> Completed
        </div>
        </div>
        <div>
        <div className="text-[40px] font-tertiary text-gradient
        mb-2">
        {
          inView ?
          <CountUp start={0} end={4} duration={3} /> :null}+
        </div>
        <div className="font-primary text-sm tracking-[2px]">Internship <br/> Done
        </div>
        </div>
        <div>
        <div className="text-[40px] font-tertiary text-gradient
        mb-2">
        {
          inView ?
          <CountUp start={0} end={8} duration={3} /> :null}+
        </div>
        <div className="font-primary text-sm tracking-[2px]">Projects <br/> Build
        </div>
        
        </div>
    </div>
    <div className='flex gap-x-8 items-center'>
        <button className="btn btn-lg">Connect me</button>
        <a href="#" className='text-gradient btn-link'>My Portfolio</a>
        </div>
    </motion.div>
    </div>
    </div>
    
  

  </section>
  );
};

export default About;
