import React from 'react';
import Image from '../assets/avtar2.png';
import { FaGithub, FaYoutube, FaDribbble } from 'react-icons/fa';
import { TypeAnimation } from 'react-type-animation';
import { motion } from 'framer-motion';
import { fadeIn } from '../variants';

const Banner = () => {
  return (
    <section className="min-h-[85vh] lg:min-h-[78vh] flex items-center" id="home">
      <div className="container mx-auto">
        <div className="flex flex-col gap-y-8 lg:flex-row lg:items-center lg:justify-between lg:gap-x-12">
          <div className='flex-1 text-center font-secondary lg:text-left'>
            <motion.h1 variants={fadeIn('up',0.3)} initial="hidden"
            whileInView={'show'} viewport={{once:false,amount:0.7}} className="text-[55px] font-bold leading-[0.8] lg:text-[110px]">
            VISHAL <span>MARKAD</span>
            </motion.h1>
            <div className="mb-6 text-[36px] lg:text-[60px] font-secondary font-semibold uppercase leading-[1]">
              <span className='text-white mr-4'>I am a</span>
              <TypeAnimation
                sequence={[
                  ' FullStack Developer',
                  2000,
                  ' Web Developer',
                  2000,
                  ' Java Developer',
                  2000,
                ]}
                speed={50}
                className="text-accent"
                wrapper="span"
                repeat={Infinity}
              />
            </div>
            <p className="mb-8 max-w-lg max-autolg:mx-0">lorem ipsum dolor sit amet, consectetur adip</p>
            <div className="flex max-w-max gap-x-6 items-center mb-12 mx-auto lg:mx-0">
              <button className="btn btn-lg">Contact me</button>
              <a href="#" className='text-gradient btn-link'>
                My Portfolio
              </a>
            </div>
            <div className="flex text-[20px] gap-x-6 max-w-max mx-auto lg:mx-0">
              <a href="#">
                <FaYoutube />
              </a>
              <a href="#">
                <FaGithub />
              </a>
              <a href="#">
                <FaDribbble />
              </a>
            </div>
          </div>

          <div className='hidden lg:flex flex-1 max-w-[320px] lg:max-w-[360px]'>
            <img
              src={Image}
              alt=""
              style={{
                width: '350px',
                height: '400px', // You can adjust the height to your preference
                borderRadius: '100%',
              }}
            />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
