/* eslint-disable jsx-a11y/anchor-is-valid */
import React from "react";
// icons
import { BsArrowUpRight } from "react-icons/bs";
// motion
import { motion } from "framer-motion";
// variants
import {fadeIn} from "../variants";

// services
const services = [
  {
    name: "Java Developer",
description:
  "I have a profound background in Java development, I've successfully developed projects, including the Placement Management System, showcasing my expertise in building efficient.",
link: "Learn more",

  },
  {
    name: "Web Development",
description:
  "I am venturing into the world of web development with a foundation in HTML, CSS, and JavaScript. Excited about creating engaging and user-friendly websites.",
link: "Learn more",

  },
  {
    name: "React.js Development",
description:
  "I am proficient in building dynamic and interactive user interfaces using React.js. With a keen eye for creating seamless user experiences.",
link: "Learn more",
  },
  {
    name: "Database Management",
description:
  "I possess foundational knowledge in database management, including MySQL, MongoDB, and Firebase. From designing relational databases to leveraging NoSQL solutions.",
link: "Learn more",

  },
];

const Services = () => {
  return (
    <section className="section" id="services">
      <div className="container mx-auto">
        <div className="flex flex-col lg:flex-row lg:mt-16">
          {/* text and images */}
          <motion.div 
          variants={fadeIn('right', 0.3)}
          initial='hidden'
          whileInView={'show'}
          viewport={{once: false, amount: 0.3}}
          className="flex-1 lg:bg-services lg:bg-bottom bg-no-repeat mix-blend-lighten mb-12 lg:mb-0">
            <h2 className="h2 text-accent mb-6 ">What I Do.</h2>
            <h3 className="h3 mb-12 max-w-[455px]">
            I am a passionate developer with a love for crafting clean and efficient code. {/*I thrive on turning innovative ideas into practical solutions.*/}
            </h3>
            <button className="btn btn-sm">See my work</button>
          </motion.div>
          {/* services */}
          <motion.div
          variants={fadeIn('left', 0.5)}
          initial='hidden'
          whileInView={'show'}
          viewport={{once: false, amount: 0.3}}
          >
            {/* services list */}
            <div className="flex-1">
              {
                services.map((service, index) => {
                  // destructure data
                  const {name, description, link} = service;
                  return (
                    <div className="border-b border-white/20 h-[142px] mb-[36px] flex" key={index}>
                      <div className="max-w-[476px]">
                        <h4 className="text-[20px] tracking-wider font-primary font-semibold mb-5">{name}</h4>
                        <p className="font-secondary leading-tight">{description}</p>
                      </div>
                      <div className="flex  flex-col flex-1 items-end">
                        <a href="#" className="btn w-9 h-9 mb-[42px] flex justify-center items-center"> <BsArrowUpRight/> </a>
                        <a href="#" className="text-gradient text-sm">{link}</a>
                      </div>
                    </div>
                  )
                })
              }
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Services;
