import React from 'react';
import { motion } from 'framer-motion';
import { fadeIn } from '../variants';
import Img1 from '../assets/portfolio-img1.png';
import Img2 from '../assets/portfolio-img2.png';
import Img3 from '../assets/portfolio-img3.png';

const Work = () => {
  return (
    <section className="section" id="work">
      <div className="container mx-auto">
        <div className="flex flex-col lg:flex-row gap-x-10 ">
          <div className="flex-1 flex flex-col gap-y-12 mb-10 lg:mb-0">
            <div>
              <h2 className="h2 leading-tight text-accent">My Latest <br /> Work</h2>
              <p className="max-w-sm mb-16">lorem ipsum dolor sit amet, consectetur adip lorem ipsum dolor sit amet, consectetur adip lorem ipsum dolor sit amet, consectetur adip lorem ipsum dolor sit amet, consectetur adiplorem ipsum dolor sit amet, consectetur adip
              </p>
              <button className='btn btn-sm'>View all projects</button>
            </div>
            <div className='group relative overflow-hidden border-2 border-white/50 rounded-X1'>

              <div className='group-hover:bg-black/70 w-full h-full absolute z-40 transition-all duration-300'></div>

              <img className='group-hover:scale-125 transition-all duration-500' src={Img1} alt="" />
              <div className='absolute bottom-0 left-12
                group-hover:bottom-12 transition-all duration-500 z-50'>
                <span className='text-gradient'>
                  Web-Development
                </span>
              </div>
              <div className='absolute bottom-0 left-12
                group-hover:bottom-0 transition-all duration-700 z-50'>
                <span className='text-3xl text-white'>Developer Portfolio</span>
              </div>
            </div>
          </div>

          <div className='flex-1 flex flex-col gap-y-10'>
            {/* Add similar code for the other images */}
            <div className='group relative overflow-hidden border-2 
              border-white/50 rounded-X1'>
              {/* Image and text elements */}
            </div>
            <div className='group relative overflow-hidden border-2 
              border-white/50 rounded-X1'>
              {/* Image and text elements */}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Work;
